import type { ButtonHTMLAttributes, MouseEvent, ReactNode } from 'react';
import { useRef, useState } from 'react';

type ButtonProps = ButtonHTMLAttributes<HTMLButtonElement> & {
  children: ReactNode;
  onClick?: () => void | ((e: MouseEvent<HTMLButtonElement>) => void) | ((e: MouseEvent) => void);
  ariaLabel: string;
  loading?: boolean;
  loader?: ReactNode;
  disabled?: boolean;
  className?: string;
  disabledIfLoading?: boolean;
  variant?: 'primary' | 'invisible';
};

const variantClasses = {
  primary:
    'relative flex gap-2 py-1 px-3 whitespace-nowrap rounded-md text-sm border-black/30 border-[1px] font-medium transition-colors ease-out shadow-inner-shadow-dark-sm bg-def-300 hover:bg-def-400 ',
  invisible: 'h-fit w-fit p-0 m-0 border-0 bg-transparent text-transparent hover:text-transparent hover:bg-transparent',
};

const Button = ({
  children,
  onClick,
  loader = <></>,
  loading = false,
  disabled = false,
  disabledIfLoading = true,
  ariaLabel,
  className = '',
  variant = 'primary',
  type = 'button',
  ...rest
}: ButtonProps) => {
  const [waves, setWaves] = useState<ReactNode[]>([]);
  const btnRef = useRef<HTMLButtonElement>(null);

  const clickHandler = (e: MouseEvent<HTMLButtonElement>) => {
    onClick && onClick(e);
    if (!btnRef.current) return;
    setWaves((waves) => [...waves, <Wave width={btnRef.current?.offsetWidth} height={btnRef.current?.offsetHeight} />]);
    setTimeout(() => {
      setWaves((waves) => waves.slice(1));
    }, 2100);
  };

  const disabledClasses =
    disabledIfLoading && loading
      ? 'cursor-wait text-def-300 bg-transparent hover:bg-transparent'
      : disabled
      ? 'cursor-not-allowed'
      : 'cursor-pointer';

  return (
    <button
      {...rest}
      onClick={clickHandler}
      type={type}
      aria-label={ariaLabel}
      disabled={disabled}
      aria-disabled={disabled}
      className={`${disabledClasses} ${variantClasses[variant]} ${className}`}
      ref={btnRef}
    >
      {children}
      {loading && loader}
      {variant !== 'invisible' &&
        waves.map((wave, i) => (
          <div key={i} className='absolute left-0 top-0'>
            {wave}
          </div>
        ))}
    </button>
  );
};

type WaveProps = {
  height?: number;
  width?: number;
};

const Wave = ({ width, height }: WaveProps) => {
  const [active, setActive] = useState(false);
  setTimeout(() => setActive(true), 10);
  return (
    <div
      className={`button-wave wave-motion-appear wave-motion rounded-md ${active ? 'wave-motion-appear-active' : ''}`}
      style={{ left: '-1px', top: '-1px', width: width || 0 + 'px', height: height || 0 + 'px' }}
    />
  );
};

export default Button;

import { ReactNode, forwardRef } from "react";

interface DrawerTriggerProps {
  children: React.ReactNode;
  className: string;
}

const DrawerTrigger = forwardRef<HTMLButtonElement, DrawerTriggerProps>(({ children, className }, ref) => {
  return (
    <button ref={ref} aria-label="Open drawer" className={`peer/drawer ${className}`}>
      {children}
    </button>
  );
});

const DrawerContent = forwardRef<HTMLDivElement, { children: ReactNode; className?: string }>(
  ({ className, children, ...props }, ref) => {
    const transforms = `transform translate-y-[100%] duration-[0.5s] ease-[cubic-bezier(0.32,0.72,0,1)]`;
    const peer = `focus-within:transform-none peer-focus/drawer:translate-y-0 peer-focus/drawer:transition-transform peer-focus/drawer:duration-[0.5s] peer-focus/drawer:ease-[cubic-bezier(0.32,0.72,0,1)]`;
    return (
      <div
        data-is="drawer-content"
        tabIndex={0}
        ref={ref}
        className={`fixed overflow-hidden w-full bottom-0 right-0 ${transforms} ${peer} ${className}`}
        {...props}
      >
        {children}
      </div>
    );
  }
);

/**
 * TODO : find a way to close the overlay when clicking on a button[data-is="drawer-close"]
 * not ready yet
 */
const DrawerOverlay = forwardRef<HTMLDivElement, { className?: string }>(({ className, ...props }, ref) => {
  return (
    <div
      ref={ref}
      data-is="overlay"
      className={`fixed top-0 left-0 peer/overlay peer-focus/drawer:w-[100vw] peer-focus/drawer:h-[100vh] has-[+:focus-within]:w-[100vw] has-[+:focus-within]:h-[100vh] ${className}`}
      {...props}
    ></div>
  );
});

const DrawerHandle = forwardRef<HTMLButtonElement, { className?: string }>((props, ref) => (
  <>
    <button
      data-is="drawer-close"
      ref={ref}
      className={`absolute top-0 h-3 w-28 m-auto block mt-4 mb-1 ${props.className}`}
    />
  </>
));

const DrawerCloseTrigger = forwardRef<HTMLButtonElement, { children: ReactNode; className?: string }>((props, ref) => {
  return (
    <button ref={ref} data-is="drawer-close" {...props}>
      {props.children}
    </button>
  );
});

const Drawer = (props: { children: ReactNode }) => {
  return (
    <>
      <style>
        {`
          [data-is="drawer-content"]:has(button[data-is="drawer-close"]:focus) {
            transform: translateY(100%);
            transition: transform 0.5s cubic-bezier(0.32, 0.72, 0, 1);
          }`}
      </style>
      {props.children}
    </>
  );
};

export { Drawer, DrawerCloseTrigger, DrawerContent, DrawerHandle, DrawerOverlay, DrawerTrigger };

